mapper.select:
SELECT
FROM b_app_user a , b_user_gym b
a.id = b.user_id and b.gym_id=#{gymId} and b.relation=#{relation} and a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	
FROM b_app_user a

WHERE a.id = #{id}

SELECT 
	
FROM b_app_user a

WHERE a.id = #{stuId}

SELECT 
	
FROM b_app_user a


	a.del_flag = #{DEL_FLAG_NORMAL} and
	a.id != 1
	
		AND a.login_name LIKE 
			'%'||#{loginName}||'%'
			'%'+#{loginName}+'%'
			concat('%',#{loginName},'%')
	
	
		AND a.nick_name LIKE 
			'%'||#{nickName}||'%'
			'%'+#{nickName}+'%'
			concat('%',#{nickName},'%')
	
	
		AND a.real_name LIKE 
			'%'||#{realName}||'%'
			'%'+#{realName}+'%'
			concat('%',#{realName},'%')
	
	
		AND a.sex = #{sex}
	
	
		AND a.token = #{token}
	
	
		AND a.phone = #{phone}
	
	
		AND a.password = #{password}
	
	
		AND a.activate_flag = #{activateFlag}
	
	
		AND a.regist_time BETWEEN #{beginRegistTime} AND #{endRegistTime}
	
	
		AND a.user_type = #{userType}
	
	
		AND a.id NOT IN
		  
            #{id}  
           
	
	
		AND a.id IN
		  
            #{inId}  
           
	
	
		AND a.user_type IN
		  
            #{type}  
           
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.create_date DESC

SELECT 
	
FROM b_app_user a


	a.del_flag = #{DEL_FLAG_NORMAL} and
	a.id != 1
	
		AND a.login_name LIKE 
			'%'||#{loginName}||'%'
